/* ==UserStyle==
@name           IETF: Dark RFC Readable
@namespace      github.com/openstyles/stylus
@version        1.0.0
@description    Makes RFC documents darker and readable
@author         0rax
==/UserStyle== */
@-moz-document url-prefix("https://tools.ietf.org/html/rfc") {
    /* Darkify RFC documents */
    * {
        margin: 0;
        padding: 0;
    }
    body {
        margin: 0;
        background-color: #333;
        color: #FFF;
    }
    a, a:visited, a.selflink {
        color: #0074D9;
    }
    div.content, .pre.noprint.docinfo {
        margin: 0 auto;
    }
    pre, span.pre {
        font-size: 1.15em;
    }
    hr {
        width: inherit !important;
    }
    div.pre.noprint.docinfo {
        position: absolute;
        width: 100%;
        left: 0px;
    }
    span.pre.noprint.docinfo {
        color: #000;
    }
    pre:first-of-type {
        margin-top: 12px;
    }
    div#legend {
        background-color: #333 !important;
        width: 270px;
    }
    /* Update colors (colors from: https://github.com/mrmrs/colors) */
    .bgblue {
        background-color: #0074D9;
    }
    .bggreen {
        background-color: #2ECC40;
    }
    .bgyellow {
        background-color: #FFDC00;
    }
    .bgorange {
        background-color: #FF851B;
    }
    .bgred {
        background-color: #FF4136;
    }
    .bgwhite {
        background-color: #FFFFFF;
    }
    .bggrey {
        background-color: #AAAAAA;
    }
    .blue {
        color: #0074D9;
    }
    .green {
        color: #2ECC40;
    }
